// A C++ program for Bellman-Ford's single source shortest path algorithm.
#include <iostream>
#include <vector>
#include <limits>

using namespace std;

// Structure to represent an edge in the graph
struct Edge {
    int src, dest, weight;
};

// Function to implement Bellman-Ford algorithm
void bellmanFord(int V, int E, vector<Edge>& edges, int src) {
    // Initialize distances from the source to all other vertices as infinite
    vector<int> dist(V, numeric_limits<int>::max());
    dist[src] = 0;

    // Relax all edges |V| - 1 times. A simple shortest path from src to any other
    // vertex can have at-most |V| - 1 edges
    for (int i = 1; i <= V - 1; i++) {
        for (int j = 0; j < E; j++) {
            int u = edges[j].src;
            int v = edges[j].dest;
            int weight = edges[j].weight;
            if (dist[u] != numeric_limits<int>::max() && dist[u] + weight < dist[v])
                dist[v] = dist[u] + weight;
        }
    }

    // Check for negative-weight cycles.  The above step guarantees shortest distances
    // if graph doesn't contain negative weight cycle.  If we get a shorter
    // path, then there is a cycle.
    for (int j = 0; j < E; j++) {
        int u = edges[j].src;
        int v = edges[j].dest;
        int weight = edges[j].weight;
        if (dist[u] != numeric_limits<int>::max() && dist[u] + weight < dist[v]) {
            cout << "Graph contains negative weight cycle" << endl;
            return;
        }
    }

    cout << "Vertex   Distance from Source" << endl;
    for (int i = 0; i < V; i++)
        cout << i << "\t\t" << dist[i] << endl;
}

// Driver program to test above function
int main() {
    int V = 5;  // Number of vertices in graph
    int E = 8;  // Number of edges in graph

    vector<Edge> edges(E);

    // Example graph (edges with weights)
    edges[0] = {0, 1, -1};
    edges[1] = {0, 2, 4};
    edges[2] = {1, 2, 3};
    edges[3] = {1, 3, 2};
    edges[4] = {1, 4, 2};
    edges[5] = {3, 2, 5};
    edges[6] = {3, 1, 1};
    edges[7] = {4, 3, -3};

    bellmanFord(V, E, edges, 0); // Source vertex is 0

    return 0;
}

floyd-warshall.code:
 #include <iostream>
#include <vector>
#include <algorithm>

using namespace std;

const int INF = 1e9; // Represents infinity (a large value indicating no path)

/*
 * Floyd-Warshall algorithm implementation to find the shortest paths between all pairs of vertices.
 * 
 * @param graph Adjacency matrix representing the graph. graph[i][j] is the weight of the edge from vertex i to vertex j.
 *              If there is no edge, graph[i][j] should be set to INF.
 * @param V Number of vertices in the graph.
 * 
 * @return A matrix representing the shortest path distances between all pairs of vertices.
 */
vector<vector<int>> floydWarshall(vector<vector<int>>& graph, int V) {
    vector<vector<int>> dist = graph; // Initialize the distance matrix with the graph

    // Iterate through all vertices as intermediate nodes
    for (int k = 0; k < V; ++k) {
        // Iterate through all vertices as source nodes
        for (int i = 0; i < V; ++i) {
            // Iterate through all vertices as destination nodes
            for (int j = 0; j < V; ++j) {
                // If vertex k provides a shorter path from i to j, update dist[i][j]
                if (dist[i][k] != INF && dist[k][j] != INF && dist[i][k] + dist[k][j] < dist[i][j]) {
                    dist[i][j] = dist[i][k] + dist[k][j];
                }
            }
        }
    }

    // Check for negative cycles
    for (int i = 0; i < V; ++i) {
        if (dist[i][i] < 0) {
            cout << "Negative cycle detected!" << endl;
            return vector<vector<int>>(); // Return empty vector to signal error
        }
    }

    return dist;
}

int main() {
    int V = 4; // Number of vertices
    vector<vector<int>> graph = {
        {0,   5,   INF, 10},
        {INF, 0,   3,   INF},
        {INF, INF, 0,   1},
        {INF, INF, INF, 0}
    };

    vector<vector<int>> shortestPaths = floydWarshall(graph, V);

    if (!shortestPaths.empty()) {
        cout << "Shortest paths between all pairs of vertices:" << endl;
        for (int i = 0; i < V; ++i) {
            for (int j = 0; j < V; ++j) {
                if (shortestPaths[i][j] == INF) {
                    cout << "INF\t";
                } else {
                    cout << shortestPaths[i][j] << "\t";
                }
            }
            cout << endl;
        }
    }

    return 0;
}

