// C++ program to implement Topological Sort
#include <iostream>
#include <vector>
#include <queue>

using namespace std;

// Function to perform topological sort
vector<int> topologicalSort(int V, vector<vector<int>>& adj) {
    vector<int> in_degree(V, 0); // Array to store in-degrees of vertices

    // Calculate in-degrees for each vertex
    for (int u = 0; u < V; u++) {
        for (int v : adj[u]) {
            in_degree[v]++;
        }
    }

    queue<int> q; // Queue to store vertices with in-degree 0

    // Add vertices with in-degree 0 to the queue
    for (int i = 0; i < V; i++) {
        if (in_degree[i] == 0) {
            q.push(i);
        }
    }

    vector<int> result; // Vector to store the topological order

    int count = 0; // Count of visited vertices

    while (!q.empty()) {
        int u = q.front();
        q.pop();
        result.push_back(u);

        // Decrease in-degree of adjacent vertices
        for (int v : adj[u]) {
            if (--in_degree[v] == 0) {
                q.push(v);
            }
        }
        count++;
    }

    // Check if there is a cycle.  If count != V, then there's a cycle
    if (count != V) {
        cout << "Error: Graph contains a cycle! Topological sort not possible.";
        return {}; // Return an empty vector to indicate failure.
    }

    return result;
}

int main() {
    int V = 6; // Number of vertices
    vector<vector<int>> adj(V); // Adjacency list representation of the graph

    // Example graph:
    // 5 -> 2
    // 5 -> 0
    // 4 -> 0
    // 4 -> 1
    // 2 -> 3
    // 3 -> 1

    adj[5].push_back(2);
    adj[5].push_back(0);
    adj[4].push_back(0);
    adj[4].push_back(1);
    adj[2].push_back(3);
    adj[3].push_back(1);

    vector<int> sorted_order = topologicalSort(V, adj);

    if (!sorted_order.empty()) {
        cout << "Topological Sort: ";
        for (int node : sorted_order) {
            cout << node << " ";
        }
        cout << endl;
    }

    return 0;
}

