#include <iostream>
#include <vector>
#include <algorithm>

using namespace std;

class Graph {
public:
    int V;
    vector<vector<int>> adj;
    vector<bool> visited;
    vector<int> disc, low, parent;
    vector<bool> ap; // articulation points

    Graph(int V) : V(V), adj(V), visited(V, false), disc(V, -1), low(V, -1), parent(V, -1), ap(V, false) {}

    void addEdge(int u, int v) {
        adj[u].push_back(v);
        adj[v].push_back(u);
    }

    // A recursive function to find articulation points using DFS
    void APUtil(int u, int &time) {
        visited[u] = true;
        disc[u] = low[u] = ++time;

        int children = 0;

        // Go through all vertices adjacent to this
        for (int v : adj[u]) {
            // If v is not visited yet, then make it a child of u
            // in DFS tree and recur for it
            if (!visited[v]) {
                children++;
                parent[v] = u;
                APUtil(v, time);

                // Check if the subtree rooted with v has a connection to
                // one of the ancestors of u
                low[u] = min(low[u], low[v]);

                // u is an articulation point in following cases

                // (1) u is root of DFS tree and has two or more children.
                if (parent[u] == -1 && children > 1)
                    ap[u] = true;

                // (2) If u is not root and low value of one of its child
                // is more than discovery value of u.
                if (parent[u] != -1 && low[v] >= disc[u])
                    ap[u] = true;
            }

            // Update low value of u for parent function calls.
            else if (v != parent[u])
                low[u] = min(low[u], disc[v]);
        }
    }

    // The main function that finds all articulation points.
    void findAPs() {
        int time = 0;
        for (int i = 0; i < V; i++) {
            if (!visited[i]) {
                APUtil(i, time);
            }
        }

        cout << "Articulation points are: ";
        for (int i = 0; i < V; i++) {
            if (ap[i]) {
                cout << i << " ";
            }
        }
        cout << endl;
    }
};

int main() {
    // Create a sample graph
    Graph g1(5);
    g1.addEdge(1, 0);
    g1.addEdge(0, 2);
    g1.addEdge(2, 1);
    g1.addEdge(0, 3);
    g1.addEdge(3, 4);
    g1.findAPs(); // Output: Articulation points are: 0 3

    Graph g2(4);
    g2.addEdge(0, 1);
    g2.addEdge(1, 2);
    g2.addEdge(2, 3);
    g2.findAPs(); // Output: Articulation points are: 1 2

    return 0;
}

