#include <iostream>
#include <vector>
#include <stack>

using namespace std;

// Function to add an edge to the graph
void addEdge(vector<vector<int>>& adj, int u, int v) {
    adj[u].push_back(v);
}

// Depth-First Search (DFS) function to populate the stack with vertices in finishing order
void dfs1(vector<vector<int>>& adj, int v, vector<bool>& visited, stack<int>& st) {
    visited[v] = true;

    for (int u : adj[v]) {
        if (!visited[u]) {
            dfs1(adj, adj[v][0], visited, st);
        }
    }
    st.push(v);
}

// Function to get the transpose of the graph
vector<vector<int>> getTranspose(vector<vector<int>>& adj, int V) {
    vector<vector<int>> transpose(V);
    for (int i = 0; i < V; i++) {
        for (int j : adj[i]) {
            transpose[j].push_back(i);
        }
    }
    return transpose;
}

// DFS function to find strongly connected components
void dfs2(vector<vector<int>>& adj, int v, vector<bool>& visited) {
    visited[v] = true;
    cout << v << " ";

    for (int u : adj[v]) {
        if (!visited[u]) {
            dfs2(adj, u, visited);
        }
    }
}

// Function to find and print strongly connected components using Kosaraju's algorithm
void findSCCs(vector<vector<int>>& adj, int V) {
    stack<int> st;
    vector<bool> visited(V, false);

    // Step 1: Populate the stack with vertices in finishing order
    for (int i = 0; i < V; i++) {
        if (!visited[i]) {
            dfs1(adj, i, visited, st);
        }
    }

    // Step 2: Get the transpose of the graph
    vector<vector<int>> transpose = getTranspose(adj, V);

    // Reset visited array
    for (int i = 0; i < V; i++) {
        visited[i] = false;
    }

    // Step 3: Process vertices in the order defined by the stack
    while (!st.empty()) {
        int v = st.top();
        st.pop();

        if (!visited[v]) {
            dfs2(transpose, v, visited);
            cout << endl;  // New line for each SCC
        }
    }
}

int main() {
    int V = 5;  // Number of vertices
    vector<vector<int>> adj(V);
    addEdge(adj, 1, 0);
    addEdge(adj, 0, 2);
    addEdge(adj, 2, 1);
    addEdge(adj, 0, 3);
    addEdge(adj, 3, 4);

    cout << "Strongly Connected Components:\n";
    findSCCs(adj, V);

    return 0;
}

