// C++ implementation of the Edmonds-Karp algorithm for Maximum Flow
#include <iostream>
#include <vector>
#include <queue>
#include <climits>

using namespace std;

// Function to perform BFS to find an augmenting path
bool bfs(vector<vector<int>>& graph, int s, int t, vector<int>& parent) {
    int n = graph.size();
    vector<bool> visited(n, false);
    queue<int> q;
    q.push(s);
    visited[s] = true;
    parent[s] = -1;

    while (!q.empty()) {
        int u = q.front();
        q.pop();

        for (int v = 0; v < n; ++v) {
            if (visited[v] == false && graph[u][v] > 0) {
                if (v == t) {
                    parent[v] = u;
                    return true;
                }
                q.push(v);
                parent[v] = u;
                visited[v] = true;
            }
        }
    }

    return false;
}

// Edmonds-Karp algorithm to find the maximum flow
int edmondsKarp(vector<vector<int>> graph, int s, int t) {
    int n = graph.size();
    vector<vector<int>> residualGraph = graph; // Create a residual graph
    vector<int> parent(n);
    int maxFlow = 0;

    // Augment the flow while there is a path from source to sink
    while (bfs(residualGraph, s, t, parent)) {
        int pathFlow = INT_MAX;
        for (int v = t; v != s; v = parent[v]) {
            int u = parent[v];
            pathFlow = min(pathFlow, residualGraph[u][v]);
        }

        // Update residual capacities of the edges and reverse edges along the path
        for (int v = t; v != s; v = parent[v]) {
            int u = parent[v];
            residualGraph[u][v] -= pathFlow;
            residualGraph[v][u] += pathFlow;
        }

        maxFlow += pathFlow;
    }

    return maxFlow;
}

int main() {
    // Example graph represented as an adjacency matrix
    vector<vector<int>> graph = {
        {0, 16, 13, 0, 0, 0},
        {0, 0, 10, 12, 0, 0},
        {0, 4, 0, 0, 14, 0},
        {0, 0, 9, 0, 0, 20},
        {0, 0, 0, 7, 0, 4},
        {0, 0, 0, 0, 0, 0}
    };

    int source = 0;  // Source node
    int sink = 5;    // Sink node

    cout << "The maximum possible flow is " << edmondsKarp(graph, source, sink) << endl;

    return 0;
}

