#include <iostream>
#include <vector>
#include <stack>

using namespace std;

// Function to check if a graph is Eulerian
bool isEulerian(const vector<vector<int>>& adj) {
    int oddDegreeCount = 0;
    for (const auto& neighbors : adj) {
        if (neighbors.size() % 2 != 0) {
            oddDegreeCount++;
        }
    }

    if (oddDegreeCount > 2) {
        return false; // More than 2 vertices with odd degree
    } else if (oddDegreeCount == 2) {
        return true; // Eulerian path exists
    } else {
        // Check if the graph is connected. If it is not connected,
        // then there is no Eulerian path or circuit
        if (adj.empty()) return true; //empty graph
        int start_node = 0; 
        while(start_node < adj.size() && adj[start_node].empty()) start_node++;
        if (start_node == adj.size()) return true; // No edges

        vector<bool> visited(adj.size(), false);
        stack<int> dfs_stack;
        dfs_stack.push(start_node);
        visited[start_node] = true;

        while(!dfs_stack.empty()) {
            int u = dfs_stack.top();
            dfs_stack.pop();
            for (int v : adj[u]) {
                if(!visited[v]) {
                    visited[v] = true;
                    dfs_stack.push(v);
                }
            }
        }

        for(int i = 0; i < adj.size(); i++) {
            if(!adj[i].empty() && !visited[i]) {
                return false;
            }
        }

        return true; // Eulerian circuit exists
    }
}

// Function to find the Eulerian path/circuit using Hierholzer's Algorithm
vector<int> findEulerianPath(vector<vector<int>> adj) {
    if (!isEulerian(adj)) {
        return {}; // No Eulerian path/circuit exists
    }

    int startNode = 0;
    int oddDegreeCount = 0;
    for(int i = 0; i < adj.size(); ++i) {
        if(adj[i].size() % 2 != 0) {
            startNode = i;
            oddDegreeCount++;
            break; //Start from a vertex with odd degree, if such a vertex exists.
        }
    }

    stack<int> path;
    vector<int> circuit;
    path.push(startNode);

    while (!path.empty()) {
        int u = path.top();
        if (!adj[u].empty()) {
            int v = adj[u].back(); // Get the last neighbor of u
            adj[u].pop_back();      // Remove the edge u-v

            //Remove u from v's adjacency list
            for(int i = 0; i < adj[v].size(); ++i) {
                if(adj[v][i] == u) {
                    adj[v].erase(adj[v].begin() + i);
                    break;
                }
            }
            path.push(v);
        } else {
            circuit.push_back(u);
            path.pop();
        }
    }
    reverse(circuit.begin(), circuit.end());
    return circuit;
}

int main() {
    // Example graph represented as an adjacency list
    vector<vector<int>> adj = {
        {1, 2},
        {0, 2, 2},
        {0, 1, 1}
    };

    vector<int> eulerianPath = findEulerianPath(adj);

    if (eulerianPath.empty()) {
        cout << "No Eulerian path/circuit exists.\n";
    } else {
        cout << "Eulerian Path/Circuit: ";
        for (int vertex : eulerianPath) {
            cout << vertex << " ";
        }
        cout << endl;
    }

    return 0;
}

