using namespace std;

// Function to compute the longest proper prefix which is also a suffix (LPS) array
vector<int> computeLPSArray(const string& pattern) {
    int m = pattern.length();
    vector<int> lps(m, 0); // Initialize LPS array with 0
    int len = 0; // Length of the previous longest prefix suffix
    int i = 1; // Index for pattern

    while (i < m) {
        if (pattern[i] == pattern[len]) {
            len++;
            lps[i] = len;
            i++;
        } else {
            if (len != 0) {
                len = lps[len - 1]; // This is tricky. Consider the example AAACAAAA and i = 7
            } else {
                lps[i] = 0;
                i++;
            }
        }
    }
    return lps;
}

// Function to implement the KMP algorithm
vector<int> KMPSearch(const string& text, const string& pattern) {
    int n = text.length();
    int m = pattern.length();
    vector<int> lps = computeLPSArray(pattern); // Compute LPS array for the pattern
    vector<int> occurrences; // Store the starting indices of occurrences

    int i = 0; // Index for text
    int j = 0; // Index for pattern

    while (i < n) {
        if (pattern[j] == text[i]) {
            i++;
            j++;
        }

        if (j == m) {
            occurrences.push_back(i - j); // Pattern found at index i-j
            j = lps[j - 1]; // Prepare j for the next possible match
        } else if (i < n && pattern[j] != text[i]) {
            // Do not match lps[0..lps[j-1]] characters, they will match anyway
            if (j != 0) {
                j = lps[j - 1];
            } else {
                i++;
            }
        }
    }
    return occurrences;
}

int main() {
    string text = "ABABDABACDABABCABAB";
    string pattern = "ABABCABAB";

    vector<int> occurrences = KMPSearch(text, pattern);

    cout << "Pattern found at indices: ";
    for (int index : occurrences) {
        cout << index << " ";
    }
    cout << endl;

    return 0;
}

rabin-karp.code:
 #include <iostream>
#include <string>
#include <vector>

using namespace std;

// Function to implement the Rabin-Karp Algorithm
vector<int> rabinKarp(const string& text, const string& pattern, int prime = 101) {
    vector<int> occurrences;
    int n = text.length();
    int m = pattern.length();

    // Basic error handling: Pattern can't be longer than text
    if (m > n) {
        return occurrences; // Return empty vector if pattern is longer than text
    }

    // Compute the hash value of the pattern
    long long patternHash = 0;
    for (int i = 0; i < m; i++) {
        patternHash = (patternHash * prime + pattern[i]) % INT_MAX; // Use INT_MAX to prevent overflow
    }

    // Compute the hash value of the first substring of the text
    long long textHash = 0;
    for (int i = 0; i < m; i++) {
        textHash = (textHash * prime + text[i]) % INT_MAX;
    }

    // Slide the pattern over the text
    for (int i = 0; i <= n - m; i++) {
        // If the hash values match, check for a true match
        if (patternHash == textHash) {
            bool match = true;
            for (int j = 0; j < m; j++) {
                if (text[i + j] != pattern[j]) {
                    match = false;
                    break;
                }
            }
            if (match) {
                occurrences.push_back(i);
            }
        }

        // Compute the hash value of the next substring of the text
        if (i < n - m) {
            textHash = (textHash - text[i] * pow(prime, m - 1)) % INT_MAX; // Remove leading digit
            textHash = (textHash * prime + text[i + m]) % INT_MAX; // Add trailing digit
            if (textHash < 0) {
               textHash += INT_MAX; // Ensure the hash value is positive.
            }
        }
    }

    return occurrences;
}

int main() {
    string text = "ABABDABACDABABCABAB";
    string pattern = "ABABCABAB";

    vector<int> occurrences = rabinKarp(text, pattern);

    if (occurrences.empty()) {
        cout << "Pattern not found in text." << endl;
    } else {
        cout << "Pattern found at index/indices: ";
        for (int index : occurrences) {
            cout << index << " ";
        }
        cout << endl;
    }

    return 0;
}

