// C++ implementation of Z algorithm
#include <iostream>
#include <vector>
#include <string>

using namespace std;

// Function to compute the Z array
vector<int> computeZArray(const string& str) {
    int n = str.length();
    vector<int> Z(n, 0);
    int left = 0, right = 0;

    for (int i = 1; i < n; ++i) {
        if (i > right) {
            // Case 1: i is outside the Z-box
            left = right = i;
            while (right < n && str[right - left] == str[right]) {
                right++;
            }
            Z[i] = right - left;
            right--;
        } else {
            // Case 2: i is inside the Z-box
            int k = i - left;
            if (Z[k] < right - i + 1) {
                // Case 2a: Z[k] is contained within the Z-box
                Z[i] = Z[k];
            } else {
                // Case 2b: Z[k] extends beyond the Z-box
                left = i;
                while (right < n && str[right - left] == str[right]) {
                    right++;
                }
                Z[i] = right - left;
                right--;
            }
        }
    }
    return Z;
}

// Function to search for a pattern in a text using Z algorithm
void searchPattern(const string& text, const string& pattern) {
    string combinedString = pattern + "$" + text; // Concatenate pattern and text with a delimiter
    int n = combinedString.length();
    vector<int> Z = computeZArray(combinedString);

    for (int i = 0; i < n; ++i) {
        if (Z[i] == pattern.length()) {
            cout << "Pattern found at index " << i - pattern.length() - 1 << endl;
        }
    }
}

int main() {
    string text = "ABABDABACDABABCABAB";
    string pattern = "ABABCABAB";

    cout << "Searching for pattern '" << pattern << "' in text '" << text << "'" << endl;
    searchPattern(text, pattern);

    return 0;
}

