// Basic Array Operations in C++
#include <iostream>
#include <vector>

using namespace std;

int main() {
    // 1. Array Initialization
    vector<int> myArray = {10, 20, 30, 40, 50};

    // 2. Accessing an element (O(1))
    int elementAtIndex2 = myArray[2];
    cout << "Element at index 2: " << elementAtIndex2 << endl;

    // 3. Modifying an element (O(1))
    myArray[1] = 25;
    cout << "Modified array: ";
    for (int num : myArray) {
        cout << num << " ";
    }
    cout << endl;

    // 4. Searching for an element (O(n))
    int searchElement = 40;
    int index = -1;
    for (int i = 0; i < myArray.size(); ++i) {
        if (myArray[i] == searchElement) {
            index = i;
            break;
        }
    }

    if (index != -1) {
        cout << "Element " << searchElement << " found at index: " << index << endl;
    } else {
        cout << "Element " << searchElement << " not found in the array." << endl;
    }

    // 5. Inserting an element at the end (O(1) amortized, O(n) if reallocation needed) - vector handles reallocation
    myArray.push_back(60);
    cout << "Array after insertion at the end: ";
    for (int num : myArray) {
        cout << num << " ";
    }
    cout << endl;

    // 6. Inserting an element at a specific index (O(n))
    myArray.insert(myArray.begin() + 3, 35); // Inserts 35 at index 3, shifting elements to the right
    cout << "Array after insertion at index 3: ";
    for (int num : myArray) {
        cout << num << " ";
    }
    cout << endl;

    // 7. Deleting an element from the end (O(1))
    myArray.pop_back();
    cout << "Array after deletion from the end: ";
    for (int num : myArray) {
        cout << num << " ";
    }
    cout << endl;

    // 8. Deleting an element from a specific index (O(n))
    myArray.erase(myArray.begin() + 2); // Deletes element at index 2, shifting elements to the left
    cout << "Array after deletion at index 2: ";
    for (int num : myArray) {
        cout << num << " ";
    }
    cout << endl;

    // 9. Array Size
    cout << "Current size of the array: " << myArray.size() << endl;

    return 0;
}

