// C++ program to count set bits in an integer using different methods
#include <iostream>
#include <algorithm> // For std::bitset (if needed for alternative approach)

// 1. Naive Approach: Iterate through bits
// Time Complexity: O(log n), where n is the number.  Each bit is checked.
// Space Complexity: O(1)
unsigned int countSetBitsNaive(unsigned int n) {
    unsigned int count = 0;
    while (n) {
        count += n & 1; // Check the last bit
        n >>= 1;      // Right shift to check the next bit
    }
    return count;
}

// 2. Brian Kernighan's Algorithm
// Time Complexity: O(number of set bits)
// Space Complexity: O(1)
unsigned int countSetBitsBrianKernighan(unsigned int n) {
    unsigned int count = 0;
    while (n) {
        n &= (n - 1); // Unsets the rightmost set bit
        count++;
    }
    return count;
}

// 3. Lookup Table Method (for smaller numbers - e.g., 8-bit integers)
// This requires pre-computation and is more suited when counting set bits repeatedly.
// Time Complexity: O(1) after precomputation
// Space Complexity: O(size of the table)

// Example with an 8-bit lookup table:
unsigned int setBitLookup[256];

void initializeLookupTable() {
    for (int i = 0; i < 256; i++) {
        setBitLookup[i] = countSetBitsNaive(i); // Initialize using the naive method
    }
}

unsigned int countSetBitsLookup(unsigned int n) {
    // This method is efficient for smaller numbers, as it performs array lookups.
    // For larger numbers, you'd need to split into bytes and sum the lookups.
    unsigned char byte1 = n & 0xFF;  // Extract the least significant byte
    unsigned char byte2 = (n >> 8) & 0xFF; // Extract the next byte
    unsigned char byte3 = (n >> 16) & 0xFF; // Extract the next byte
    unsigned char byte4 = (n >> 24) & 0xFF; // Extract the most significant byte
    return setBitLookup[byte1] + setBitLookup[byte2] + setBitLookup[byte3] + setBitLookup[byte4];
}


// 4. Using built-in __builtin_popcount() (GCC specific)
// Time Complexity: O(1) - generally uses hardware instructions if available
// Space Complexity: O(1)
#ifdef __GNUC__ // Check if it's a GCC compiler
unsigned int countSetBitsBuiltin(unsigned int n) {
    return __builtin_popcount(n); // Requires GCC compiler
}
#endif


int main() {
    unsigned int num = 7; // Example number

    std::cout << "Number: " << num << std::endl;

    std::cout << "Naive Approach: " << countSetBitsNaive(num) << std::endl;
    std::cout << "Brian Kernighan's Algorithm: " << countSetBitsBrianKernighan(num) << std::endl;

    // Example using Lookup Table:
    initializeLookupTable(); // Must be called before using countSetBitsLookup
    std::cout << "Lookup Table Approach: " << countSetBitsLookup(num) << std::endl;

#ifdef __GNUC__
    std::cout << "Built-in Approach: " << countSetBitsBuiltin(num) << std::endl;
#endif

    return 0;
}

