#include <iostream>
#include <vector>
#include <algorithm>

using namespace std;

// Function to calculate the minimum cost to visit all cities exactly once.
// This is a simplified version of the Traveling Salesman Problem (TSP).

int tsp(int mask, int pos, vector<vector<int>>& dist, vector<vector<int>>& dp, int n) {
  // Base case: All cities have been visited
  if (mask == (1 << n) - 1) {
    return dist[pos][0]; // Return to the starting city (city 0)
  }

  // If the result is already calculated, return it
  if (dp[mask][pos] != -1) {
    return dp[mask][pos];
  }

  int ans = INT_MAX;

  // Try to visit all unvisited cities
  for (int city = 0; city < n; city++) {
    // If the city is not visited
    if ((mask & (1 << city)) == 0) {
      int newAns = dist[pos][city] + tsp(mask | (1 << city), city, dist, dp, n);
      ans = min(ans, newAns);
    }
  }

  // Store the result in the dp table and return it
  return dp[mask][pos] = ans;
}

int main() {
  int n = 4; // Number of cities

  // dist[i][j] is the distance from city i to city j
  vector<vector<int>> dist = {
    {0, 10, 15, 20},
    {10, 0, 35, 25},
    {15, 35, 0, 30},
    {20, 25, 30, 0}
  };

  // dp[mask][pos] stores the minimum cost to visit all cities represented by the mask,
  // starting from the city at position 'pos'.
  vector<vector<int>> dp((1 << n), vector<int>(n, -1));

  // Call the TSP function starting from city 0 with an initial mask of 1 (city 0 visited).
  cout << "Minimum cost to visit all cities: " << tsp(1, 0, dist, dp, n) << endl;

  return 0;
}

