 #include <iostream>
#include <queue>

// Define a structure for a tree node
struct TreeNode {
    int data;
    TreeNode* left;
    TreeNode* right;

    TreeNode(int val) : data(val), left(nullptr), right(nullptr) {}
};

class BinaryTree {
public:
    TreeNode* root;

    BinaryTree() : root(nullptr) {}

    // Function to insert a node into the tree (iterative level order insertion)
    void insert(int data) {
        TreeNode* newNode = new TreeNode(data);
        if (!root) {
            root = newNode;
            return;
        }

        std::queue<TreeNode*> q;
        q.push(root);

        while (!q.empty()) {
            TreeNode* current = q.front();
            q.pop();

            if (!current->left) {
                current->left = newNode;
                return;
            } else {
                q.push(current->left);
            }

            if (!current->right) {
                current->right = newNode;
                return;
            } else {
                q.push(current->right);
            }
        }
    }

    // Function to perform an inorder traversal of the tree
    void inorderTraversal(TreeNode* node) {
        if (node) {
            inorderTraversal(node->left);
            std::cout << node->data << " ";
            inorderTraversal(node->right);
        }
    }

    // Function to search for a node in the tree (simple recursive search)
    TreeNode* search(TreeNode* node, int key) {
        if (node == nullptr || node->data == key) {
            return node;
        }

        TreeNode* leftResult = search(node->left, key);
        if (leftResult != nullptr) return leftResult;

        return search(node->right, key);
    }

};

int main() {
    // Create a binary tree
    BinaryTree tree;

    // Insert some nodes
    tree.insert(1);
    tree.insert(2);
    tree.insert(3);
    tree.insert(4);
    tree.insert(5);
    tree.insert(6);

    // Perform inorder traversal
    std::cout << "Inorder Traversal: ";
    tree.inorderTraversal(tree.root);
    std::cout << std::endl;

    // Search for a node
    int searchKey = 4;
    TreeNode* foundNode = tree.search(tree.root, searchKey);
    if (foundNode) {
        std::cout << "Node with data " << searchKey << " found." << std::endl;
    } else {
        std::cout << "Node with data " << searchKey << " not found." << std::endl;
    }

    return 0;
}

