 #include <iostream>
#include <stack>

struct Node {
    int data;
    Node* left;
    Node* right;

    Node(int data) : data(data), left(nullptr), right(nullptr) {}
};

// Recursive Inorder Traversal
void inorderRecursive(Node* root) {
    if (root == nullptr) {
        return;
    }

    inorderRecursive(root->left); // Traverse the left subtree
    std::cout << root->data << " ";  // Visit the root node
    inorderRecursive(root->right); // Traverse the right subtree
}

// Iterative Inorder Traversal using a stack
void inorderIterative(Node* root) {
    std::stack<Node*> stack;
    Node* current = root;

    while (current != nullptr || !stack.empty()) {
        // Reach the leftmost node of the current node
        while (current != nullptr) {
            stack.push(current);
            current = current->left;
        }

        // Current is nullptr, so pop from stack and print
        current = stack.top();
        stack.pop();

        std::cout << current->data << " ";

        // Now visit the right subtree
        current = current->right;
    }
}

int main() {
    // Example Binary Search Tree:
    //       4
    //      / \
    //     2   5
    //    / \
    //   1   3
    Node* root = new Node(4);
    root->left = new Node(2);
    root->right = new Node(5);
    root->left->left = new Node(1);
    root->left->right = new Node(3);

    std::cout << "Recursive Inorder Traversal: ";
    inorderRecursive(root);
    std::cout << std::endl;

    std::cout << "Iterative Inorder Traversal: ";
    inorderIterative(root);
    std::cout << std::endl;

    // Clean up memory (important to prevent memory leaks!)
    delete root->left->left; 
    delete root->left->right;
    delete root->left;
    delete root->right;
    delete root;

    return 0;
}

