#include <iostream>

struct Node {
    int data;
    Node* left;
    Node* right;
    bool leftThread;
    bool rightThread;

    Node(int val) : data(val), left(nullptr), right(nullptr), leftThread(false), rightThread(false) {}
};

class ThreadedBinaryTree {
public:
    Node* root;

    ThreadedBinaryTree() : root(nullptr) {}

    // Inorder successor
    Node* inorderSuccessor(Node* node) {
        // If right thread exists, return the node pointed by it
        if (node->rightThread) {
            return node->right;
        }

        // Else return leftmost child of right subtree
        Node* current = node->right;
        while (current != nullptr && !current->leftThread)
            current = current->left;
        return current;
    }

    // Inserts a new node with the given key
    void insert(int key) {
        Node* newNode = new Node(key);

        if (root == nullptr) {
            root = newNode;
            return;
        }

        Node* current = root;
        Node* parent = nullptr;

        while (current != nullptr) {
            parent = current;

            if (key < current->data) {
                if (current->leftThread) {
                    break;
                } else {
                    current = current->left;
                }
            } else {
                if (current->rightThread) {
                    break;
                } else {
                    current = current->right;
                }
            }
        }

        if (key < parent->data) {
            newNode->right = parent;
            newNode->left = parent->left;
            parent->left = newNode;
            parent->leftThread = false;
            newNode->rightThread = true;
        } else {
            newNode->left = parent;
            newNode->right = parent->right;
            parent->right = newNode;
            parent->rightThread = false;
            newNode->leftThread = true;
        }
    }

    // Inorder traversal using threads
    void inorderTraversal() {
        if (root == nullptr) return;

        // Find the leftmost node
        Node* current = root;
        while (current->left != nullptr && !current->leftThread) {
            current = current->left;
        }

        // One by one print successors
        while (current != nullptr) {
            std::cout << current->data << " ";
            current = inorderSuccessor(current);
        }
        std::cout << std::endl;
    }
};

int main() {
    ThreadedBinaryTree tree;
    tree.insert(20);
    tree.insert(10);
    tree.insert(30);
    tree.insert(5);
    tree.insert(16);
    tree.insert(14);
    tree.insert(40);
    tree.insert(25);

    std::cout << "Inorder traversal of the threaded binary tree: ";
    tree.inorderTraversal(); // Output: 5 10 14 16 20 25 30 40 

    return 0;
}

