// C++ implementation of Binary Search
#include <iostream>
#include <vector>

using namespace std;

// Function to perform binary search in a sorted array
int binarySearch(const vector<int>& arr, int target) {
    int low = 0;                  // Initialize the lower bound of the search space
    int high = arr.size() - 1;    // Initialize the upper bound of the search space

    while (low <= high) {         // Continue searching as long as the search space is not empty
        int mid = low + (high - low) / 2;  // Calculate the middle index to prevent potential overflow

        if (arr[mid] == target) {   // If the middle element is the target, return its index
            return mid;             
        } else if (arr[mid] < target) { // If the target is greater than the middle element, search in the right half
            low = mid + 1;        
        } else {                     // If the target is less than the middle element, search in the left half
            high = mid - 1;       
        }
    }

    return -1; // If the target is not found, return -1
}

int main() {
    vector<int> sortedArray = {2, 5, 8, 12, 16, 23, 38, 56, 72, 91};
    int targetValue = 23;

    int result = binarySearch(sortedArray, targetValue);

    if (result == -1) {
        cout << "Element is not found in the array" << endl;
    } else {
        cout << "Element is found at index: " << result << endl;
    }

    return 0;
}

