// C++ implementation of Kadane's Algorithm
#include <iostream>
#include <vector>
#include <algorithm>

using namespace std;

int kadane(const vector<int>& arr) {
    // Initialize variables to store the maximum sum found so far and the current maximum sum.
    int max_so_far = arr[0]; // Initialize with the first element to handle cases where all elements are negative
    int current_max = arr[0];

    // Iterate through the array starting from the second element.
    for (size_t i = 1; i < arr.size(); ++i) {
        // Update current_max: either extend the previous subarray or start a new subarray from the current element.
        current_max = max(arr[i], current_max + arr[i]);

        // Update max_so_far if the current maximum sum is greater.
        max_so_far = max(max_so_far, current_max);
    }

    // Return the maximum sum found.
    return max_so_far;
}

int main() {
    // Example usage
    vector<int> arr = {-2, -3, 4, -1, -2, 1, 5, -3};  // Example array

    // Calculate the maximum sum using Kadane's algorithm.
    int max_sum = kadane(arr);

    // Print the maximum sum to the console.
    cout << "Maximum contiguous sum is " << max_sum << endl;

    vector<int> arr2 = {-1, -2, -3, -4}; // Test case with all negative numbers
    int max_sum2 = kadane(arr2);
    cout << "Maximum contiguous sum is " << max_sum2 << endl; // Expected output: -1

    vector<int> arr3 = {1, 2, 3, 4}; // Test case with all positive numbers
    int max_sum3 = kadane(arr3);
    cout << "Maximum contiguous sum is " << max_sum3 << endl; // Expected output: 10


    return 0;
}

