#include <iostream>

// Function to calculate base^exp % mod efficiently using fast exponentiation
long long fast_exponentiation(long long base, long long exp, long long mod) {
 long long res = 1; // Initialize result to 1
 base = base % mod; // Update base if it is more than or equal to mod

 while (exp > 0) {
  // If exp is odd, multiply base with result
  if (exp % 2 == 1) {
   res = (res * base) % mod;
  }

  // exp must be even now
  exp = exp >> 1; // exp = exp/2
  base = (base * base) % mod;
 }
 return res;
}

int main() {
 long long base = 2; // The base number
 long long exponent = 10; // The exponent
 long long modulus = 1000000007; // A large prime number for modular arithmetic

 long long result = fast_exponentiation(base, exponent, modulus);

 std::cout << base << "^" << exponent << " mod " << modulus << " = " << result << std::endl;

 base = 5;
 exponent = 3;
 modulus = 13;

 result = fast_exponentiation(base, exponent, modulus);
 std::cout << base << "^" << exponent << " mod " << modulus << " = " << result << std::endl;
 return 0;
}

