// Randomized QuickSort Implementation
#include <iostream>
#include <vector>
#include <algorithm>
#include <random>

using namespace std;

// Function to partition the array using the last element as the pivot
int partition(vector<int>& arr, int low, int high) {
    // Choose a random pivot index
    random_device rd;
    mt19937 gen(rd());
    uniform_int_distribution<> distrib(low, high);
    int randomIndex = distrib(gen);

    // Swap random element with last element (pivot)
    swap(arr[randomIndex], arr[high]);

    int pivot = arr[high]; // pivot
    int i = (low - 1); // Index of smaller element

    for (int j = low; j <= high - 1; j++) {
        // If current element is smaller than the pivot
        if (arr[j] < pivot) {
            i++; // increment index of smaller element
            swap(arr[i], arr[j]);
        }
    }
    swap(arr[i + 1], arr[high]);
    return (i + 1);
}

// Function to implement quicksort
void quickSort(vector<int>& arr, int low, int high) {
    if (low < high) {
        // pi is partitioning index, arr[p] is now at right place
        int pi = partition(arr, low, high);

        // Separately sort elements before partition and after partition
        quickSort(arr, low, pi - 1);
        quickSort(arr, pi + 1, high);
    }
}

// Function to print an array
void printArray(const vector<int>& arr) {
    for (int num : arr) {
        cout << num << " ";
    }
    cout << endl;
}

int main() {
    vector<int> arr = {10, 7, 8, 9, 1, 5};
    int n = arr.size();
    cout << "Unsorted array: \n";
    printArray(arr);

    quickSort(arr, 0, n - 1);

    cout << "Sorted array: \n";
    printArray(arr);
    return 0;
}

lru-cache-implementation.code:
 #include <iostream>
#include <unordered_map>

using namespace std;

// Node structure for the doubly linked list
struct Node {
    int key;
    int value;
    Node* prev;
    Node* next;
    Node(int k, int v) : key(k), value(v), prev(nullptr), next(nullptr) {}
};

class LRUCache {
private:
    int capacity;
    unordered_map<int, Node*> cache;
    Node* head;
    Node* tail;

    // Helper function to move a node to the head of the list (most recently used)
    void moveToHead(Node* node) {
        if (node == head) return;

        if (node == tail) {
            tail = node->prev;
            tail->next = nullptr;
        } else {
            node->prev->next = node->next;
            node->next->prev = node->prev;
        }

        node->next = head;
        node->prev = nullptr;
        head->prev = node;
        head = node;
    }

public:
    LRUCache(int capacity) : capacity(capacity) {
        head = nullptr;
        tail = nullptr;
    }

    int get(int key) {
        if (cache.find(key) != cache.end()) {
            Node* node = cache[key];
            moveToHead(node);
            return node->value;
        } else {
            return -1; // Key not found
        }
    }

    void put(int key, int value) {
        if (cache.find(key) != cache.end()) {
            // Key already exists, update the value and move to head
            Node* node = cache[key];
            node->value = value;
            moveToHead(node);
        } else {
            // Key doesn't exist, create a new node
            Node* newNode = new Node(key, value);
            cache[key] = newNode;

            if (cache.size() > capacity) {
                // Evict the least recently used node (tail)
                cache.erase(tail->key);
                Node* temp = tail;
                tail = tail->prev;
                if (tail) {
                    tail->next = nullptr;
                }
                delete temp;
                temp = nullptr;
                if(cache.size() == 0){
                    head = nullptr;
                    tail = nullptr;
                }
            }

            if (head == nullptr) {
                // Cache is empty
                head = newNode;
                tail = newNode;
            } else {
                // Add the new node to the head
                newNode->next = head;
                head->prev = newNode;
                head = newNode;
            }
        }
    }
};

int main() {
    LRUCache cache(2); // Capacity of 2

    cache.put(1, 1);
    cache.put(2, 2);
    cout << cache.get(1) << endl;       // returns 1
    cache.put(3, 3);    // evicts key 2
    cout << cache.get(2) << endl;       // returns -1 (not found)
    cache.put(4, 4);    // evicts key 1
    cout << cache.get(1) << endl;       // returns -1 (not found)
    cout << cache.get(3) << endl;       // returns 3
    cout << cache.get(4) << endl;       // returns 4

    return 0;
}

