// Singleton Pattern Implementation
#include <iostream>
#include <mutex>

class Singleton {
private:
    // Private constructor to prevent instantiation from outside the class.
    Singleton() {
        std::cout << "Singleton instance created.\n";
    }

    // Delete copy constructor and assignment operator.
    Singleton(const Singleton&) = delete;
    Singleton& operator=(const Singleton&) = delete;

    // Static pointer to the single instance.
    static Singleton* instance;

    // Mutex for thread safety.
    static std::mutex mutex;

public:
    // Static method to get the singleton instance.
    static Singleton* getInstance() {
        // Use double-checked locking to ensure thread safety and efficiency.
        std::lock_guard<std::mutex> lock(mutex);
        if (instance == nullptr) {
            instance = new Singleton();
        }
        return instance;
    }

    // Example method to demonstrate usage.
    void doSomething() {
        std::cout << "Singleton is doing something!\n";
    }

    ~Singleton() {
        std::cout << "Singleton instance destroyed.\n";
    }
};

// Initialize the static pointer to nullptr.
Singleton* Singleton::instance = nullptr;

// Initialize the static mutex.
std::mutex Singleton::mutex;

int main() {
    // Get the singleton instance.
    Singleton* singleton1 = Singleton::getInstance();
    singleton1->doSomething();

    // Get the singleton instance again - it will be the same instance.
    Singleton* singleton2 = Singleton::getInstance();
    singleton2->doSomething();

    // Verify that both pointers point to the same instance.
    if (singleton1 == singleton2) {
        std::cout << "Both pointers point to the same instance.\n";
    }

    // No need to explicitly delete singleton1 or singleton2 because the destructor should be called when the program ends. However, for cleaner code, one should manage the instance and potentially delete it when no longer required.
    // Uncomment the below lines when you are sure that the instance won't be used again.
    // std::lock_guard<std::mutex> lock(Singleton::mutex); // protect the deletion
    // delete Singleton::instance;
    // Singleton::instance = nullptr;  // reset the pointer after deletion.

    return 0;
}

