/*
 This code provides examples of how to analyze time and space complexity using Big O notation.
 It includes common examples to demonstrate the concepts.
*/

#include <iostream>
#include <vector>

// Example 1: O(1) - Constant Time and Space
void constantTime(int arr[], int n) {
 // This operation takes constant time, regardless of the input size.
 int firstElement = arr[0]; // Accessing the first element of the array. Time: O(1)
 std::cout << "First Element: " << firstElement << std::endl;
 //The function uses a constant amount of extra memory regardless of input size. space : O(1).
}

// Example 2: O(n) - Linear Time and Constant Space
void linearTime(int arr[], int n) {
 // This loop iterates through each element of the array once.
 for (int i = 0; i < n; ++i) { // Time: O(n)
 std::cout << arr[i] << " ";
 }
 std::cout << std::endl;
 //The function uses a constant amount of extra memory regardless of input size. space : O(1).
}

// Example 3: O(n^2) - Quadratic Time and Constant Space
void quadraticTime(int arr[], int n) {
 // Nested loops iterate through all possible pairs of elements in the array.
 for (int i = 0; i < n; ++i) {
 for (int j = 0; j < n; ++j) { // Time: O(n*n) = O(n^2)
 std::cout << arr[i] * arr[j] << " ";
 }
 std::cout << std::endl;
 }
 //The function uses a constant amount of extra memory regardless of input size. space : O(1).
}

// Example 4: O(n) - Linear Time and Linear Space
std::vector<int> linearSpace(int arr[], int n) {
 // This function creates a new vector of size n.
 std::vector<int> newArr(n); // Space: O(n)
 for (int i = 0; i < n; ++i) { // Time: O(n)
 newArr[i] = arr[i] * 2;
 }
 return newArr;
}

//Example 5: O(log n) - Logarithmic Time
int binarySearch(int arr[], int low, int high, int target){
 while(low <= high){
 int mid = low + (high - low) / 2;
 if(arr[mid] == target)
 return mid;
 else if (arr[mid] < target)
 low = mid + 1;
 else
 high = mid -1;
 }
 return -1; //Not found
}

int main() {
 int arr[] = {1, 2, 3, 4, 5};
 int n = sizeof(arr) / sizeof(arr[0]);

 std::cout << "Constant Time Example: ";
 constantTime(arr, n);

 std::cout << "Linear Time Example: ";
 linearTime(arr, n);

 std::cout << "Quadratic Time Example: " << std::endl;
 quadraticTime(arr, n);

 std::cout << "Linear Space Example: ";
 std::vector<int> newArr = linearSpace(arr, n);
 for (int i = 0; i < n; ++i) {
 std::cout << newArr[i] << " ";
 }
 std::cout << std::endl;

 int target = 3;
 int result = binarySearch(arr, 0, n-1, target);
 if(result == -1)
 std::cout << "Element is not found!";
 else
 std::cout << "Element is found at index: " << result << std::endl;

 return 0;
}

