// C++ implementation of an Edge for Graph Representation
#include <iostream>
#include <string>

class Edge {
public:
    int source;    // Vertex where the edge originates
    int destination; // Vertex where the edge terminates
    int weight;      // Weight or cost associated with the edge (optional)

    // Default constructor
    Edge() : source(0), destination(0), weight(0) {}

    // Parameterized constructor
    Edge(int src, int dest, int w = 1) : source(src), destination(dest), weight(w) {}

    // Overload the output stream operator to print the edge
    friend std::ostream& operator<<(std::ostream& os, const Edge& edge) {
        os << "(" << edge.source << " -> " << edge.destination << ", Weight: " << edge.weight << ")";
        return os;
    }

    // Example usage: Check if two edges are equal
    bool operator==(const Edge& other) const {
        return (source == other.source && destination == other.destination && weight == other.weight);
    }
};

// Main function for demonstration
int main() {
    // Create some edge objects
    Edge edge1(0, 1, 5); // Edge from vertex 0 to 1 with weight 5
    Edge edge2(1, 2);    // Edge from vertex 1 to 2 with default weight 1
    Edge edge3(0, 1, 5);

    // Print the edges
    std::cout << "Edge 1: " << edge1 << std::endl;
    std::cout << "Edge 2: " << edge2 << std::endl;

    // Compare two edges
    if (edge1 == edge3) {
        std::cout << "Edge 1 and Edge 3 are equal." << std::endl;
    } else {
        std::cout << "Edge 1 and Edge 3 are not equal." << std::endl;
    }

    return 0;
}
